  void addComponente( Component comp,int gridx,int gridy,
        int gridw,int gridh ) {
        GridBagConstraints gbc = new GridBagConstraints();
        gbc.gridx = gridx;
        gbc.gridy = gridy;
        gbc.gridwidth = gridw;
        gbc.gridheight = gridh;
        ((GridBagLayout)this.getLayout()).setConstraints(comp,gbc );
        add( comp );
   }

